import React from "react";
import Webcam from "react-webcam";

class webcamComponent extends React.Component {

    setRef = webcam => {
        this.webcam = webcam;
    }

    captureImage = () => {
        console.log("Capturing Image"); 
        const image = this.webcam.getScreenshot();
        const imageArray = this.convertToByteArray(image);
        this.fetchData(imageArray);
    }

    convertToByteArray = (image) => {
        const base64 = require('base64-js');
        const base64String = image.split(',')[1];
        return base64.toByteArray(base64String);
    }

    fetchData = (imageArray) => {
        console.log("Sending Image to Azure"); 
        const apiKey = '062522cd19d548218826e12756809e84';
        const apiEndpoint = 'https://australiaeast.api.cognitive.microsoft.com/vision/v2.0/read/core/asyncBatchAnalyze"';
        fetch(apiEndpoint, {
            body: imageArray,
            header: {
                'Content-Type': 'application/octet-stream', 'Ocp-Apim-Subscription-Key': apiKey
            },
            method: 'POST'
        })
        /*const apiKey = 'b2479f9ef5ed4bf5bb59dd13bf1354b5';
        const apiEndpoint = 'https://australiaeast.api.cognitive.microsoft.com/face/v1.0/detect?returnFaceAttributes=emotion';
        // the post request
        fetch(apiEndpoint, {
            body: imageArray,
            headers: {
                'cache-control': 'no-cache', 'Ocp-Apim-Subscription-Key': apiKey, 'Content-Type': 'application/octet-stream'
            },
            method: 'POST'
        }).then(response => {
            if (response.ok) {
                response.json().then(data => {

                });
            }
        })*/;
    }

    render() {
        const videoConstraints = {
            width: 750,
            height: 500,
            facingMode: "user"
        };

        return (
            <div>
                <div>
                    <Webcam
                        audio={false}
                        height={250}
                        weight={375}
                        ref={this.setRef}
                        screenshotFrame="image/jpeg"
                        videoConstraints={videoConstraints}
                    />
                    </div>
                <button variant="primary" onClick={this.captureImage}>Capture Text</button>
            </div>
        );
    }
    
}

export default webcamComponent;
