import React from 'react';

class displayText extends React.Component {

    constructor(props) {
        super(props);
        this.text_ref = React.createRef();
        this.text = "";
    }

    checkText = () => {
        const textNode = this.text_ref.current;
        if (nodeText.textContent == "Open the menu to scan and add some text...") {
            textNode.textContent = "";
            textNode.style.color = "black";
        }
    }

    lostFocus = () => {
        const textNode = this.text_ref.current;
        if (this.text == "") {
            textNode.textContent = "Open the menu to scan and add some text...";
            textNode.style.color = "grey";
        }
        this.forceUpdate();
    }

    render() {
        return (
            <div>
                <p ref={this.text_ref} className="mainText" onBlur={this.lostFocus} onClick={this.checkText} contentEditable="true" suppressContentEditableWarning={true}>
                    Open the menu to scan and add some text...
                </p>
            </div >
        );
    }
}

export default displayText
