import React, { Component } from 'react';
import './components/stylesheet.css';
import WebcamComponent from './components/webcamComponent'
import DisplayText from './components/displayText'
import menuButton from './menuButton.png';

class App extends Component {
    
    constructor(props) {
        super(props);
        this.screen = 0;
        this.menuState = false;
    }

    changeScreen = () => {
        switch (this.screen) {
            case 0:
                this.screen += 1;
                break
            case 1:
                this.screen -= 1;
                break
        }
        console.log("Screen = " + this.screen);
        this.forceUpdate();
    }

    getScreen = () => {
        return this.screen;
    }

    getMenuState = () => {
        return this.menuState;
    }

    menuClick = () => {
        console.log("Menu Clicked");
    }

    render() {
        const screen = this.getScreen();
        const menuState = this.getMenuState();
        if (screen == 0) {
            return (
                    <DisplayText /> 
            );
        }
        if (screen == 1) {
            return (
                    <WebcamComponent />
            );
        }
        if (menuState == false) {
            return (
                <img className="menuImage" src={menuButton} onClick={this.menuClick} />
            )
        }
    }
}

export default App;
