import React, { Component } from 'react';
import './components/stylesheet.css';
import WebcamComponent from './components/webcamComponent'
import DisplayText from './components/displayText'
import menuButton from './menuButton.png';

class App extends Component {
    
    constructor(props) {
        super(props);
        this.screen = 0;
        this.menuState = "hidden";

        this.myRef = React.createRef();
    }

    changeScreen = () => {
        switch (this.screen) {
            case 0:
                this.screen += 1;
                break
            case 1:
                this.screen -= 1;
                break
        }
        console.log("Screen = " + this.screen);
        this.forceUpdate();
    }

    getScreen = () => {
        return this.screen;
    }

    getMenuState = () => {
        this.refs.mene
        console.log(this.menuState);
        return this.menuState;
    }

    menuClick = () => {
        const node = this.myRef.current;
        node.style.visibility = "hidden";
        this.forceUpdate();
    }

    render() {
        const screen = this.getScreen();
        //const menuState = this.getMenuState();
        if (screen == 0) {
            return (
                <div >
                    <DisplayText /> 
                    <img ref={this.myRef} className="menuImage" src={menuButton} onClick={this.menuClick} />
                    <div className="menu">
                        <button>TEXT</button>
                    </div>
                </div >
            );
        }
        if (screen == 1) {
            return (
                <div >
                    <WebcamComponent />
                    <img className="menu" src={menuButton} onClick={this.menuClick} />
                    <div className="menu">
                        <button>TEXT</button>
                    </div>
                </div >
            );
        }
    }
}

export default App;
