import React, { Component } from 'react';
import MetaTags from 'react-meta-tags';
import './components/stylesheet.css';
import WebcamComponent from './components/webcamComponent'
import DisplayText from './components/displayText'
import MenuComponent from './components/menuComponent'

class App extends Component {
    
    constructor(props) {
        super(props);
        this.screen = 0;

        this.text_ref = React.createRef();
        this.textState = false;
        this.text = "Open the menu to add some text..."
    }

    changeScreen = (screenNum) => {
        this.screen = screenNum;
        this.forceUpdate();
    }

    getScreen = () => {
        return this.screen;
    }

    getTextState = () => {
        return this.textState;
    }

    setText = () => {
        console.log("setText()");
        const textNode = this.text_ref.current;
        textNode.style.visibility = "hidden";
        if (this.textState) {
            textNode.style.color = "black";
        } else {
            textNode.style.color = "grey";
        }
    }

    /*selectText = () => {
        console.log("Selecting all Text");
        const textNode = this.text_ref.current;
        document.execCommand("copy", true, null);
    }*/

    /*copyText = () => { 
        this.textState = false;
        this.text = "Open the menu to add some text...";
        this.setText();
    }*/
    

    render() {
        const screen = this.getScreen();
        //const textState = this.getTextState();

        if (screen === 0) {
            return (
                <div >
                    <MetaTags>
                        <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1" />
                        <title>Notes Converter</title>
                    </MetaTags>
                    <DisplayText ref={this.text_ref} /> 
                    <MenuComponent ref={this.menu_ref} changeScreen={this.changeScreen} /> 
                </div >
            );
        }
        if (screen === 1) {
            return (
                <div >
                    <MetaTags>
                        <meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1"/>
                            <title>Notes Converter</title>
                    </MetaTags>
                    <MenuComponent ref={this.menu_ref} changeScreen={this.changeScreen} /> 
                    <div className="webcamContainer" visibility="visible">
                        <WebcamComponent />
                    </div>
                </div >
            );
        }

        this.setText() ; // NOT WORKING
    }
}

export default App;
