import React from 'react';
import Webcam from "react-webcam";

class webcamComponent extends React.Component {

    // setRef is used to create a reference to the webcam DOM node so that it can be accessed directly
    setRef = webcam => {
        this.webcam = webcam;
    }

    captureImage = () => {
        const image = this.webcam.getScreenshot();
        console.log(image);
    }

    render() {
        return (
            <div>
                <button variant="primary" onClick={this.captureImage}>Capture Image of Notes</button>
            </div >
        );
    }
}

export default webcamComponent
