import React, { Component } from 'react';
import './components/stylesheet.css';
import WebcamComponent from './components/webcamComponent'
import DisplayText from './components/displayText'
import hamButton from './hamButton.png';
import crossButton from './crossButton.png';

class App extends Component {
    
    constructor(props) {
        super(props);
        this.screen = 0;
        this.menuState = "hidden";
        this.ham_button_ref = React.createRef();
        this.cross_button_ref = React.createRef();
        this.menu_ref = React.createRef();
    }

    changeScreen = () => {
        switch (this.screen) {
            case 0:
                this.screen += 1;
                break
            case 1:
                this.screen -= 1;
                break
        }
        console.log("Screen = " + this.screen);
        this.forceUpdate();
    }

    getScreen = () => {
        return this.screen;
    }

    getMenuState = () => {
        console.log(this.menuState);
        return this.menuState;
    }

    hamClick = () => {
        const hamNode = this.ham_button_ref.current;
        const menuNode = this.menu_ref.current;
        hamNode.style.visibility = "hidden";
        menuNode.style.visibility = "visible";
        this.forceUpdate();
    }

    crossClick = () => {
        //const node = this.cross_button_ref.current;
        //node.style.visibility = "hidden";
        //this.forceUpdate();
    }

    render() {
        const screen = this.getScreen();
        //const menuState = this.getMenuState();
        if (screen == 0) {
            return (
                <div >
                    <DisplayText /> 
                    <img ref={this.ham_button_ref} className="hamImage" src={hamButton} onClick={this.hamClick} />
                    <div ref={this.menu_ref} className="menu">
                        <img ref={this.cross_button_ref} className="crossImage" src={crossButton} />
                    </div>
                </div >
            );
        }
        if (screen == 1) {
            return (
                <div >
                    <WebcamComponent />
                    <img ref={this.ham_button} className="hamImage" src={hamButton} onClick={this.menuClick} />
                    <div className="menu">
                        <img ref={this.cross_button} className="crossImage" src={crossButton} />
                    </div>
                </div >
            );
        }
    }
}

export default App;
