import React, { Component } from 'react';
import './components/stylesheet.css';
import WebcamComponent from './components/webcamComponent'
import DisplayText from './components/displayText'

class App extends Component {
    
    constructor(props) {
        super(props);
        this.screen = 0;
        /*this.state = {
            theDataFromChild: null
        };*/
    }

    /*myCallback = (dataFromChild) => {
        this.setState({ theDataFromChild: dataFromChild });
    }*/
    changeScreen = () => {
        switch (this.screen) {
            case 0:
                this.screen += 1;
                break
            case 1:
                this.screen -= 1;
                break
        }
        console.log("Screen = " + this.screen);
        this.forceUpdate();
    }

    getScreen = () => {
        return this.screen;
    }

    render() {
        const screen = this.getScreen();
        console.log(screen);
        if (screen == 0) {
            return (
                <div >
                    <DisplayText /> {/*callbackFromParent={this.myCallback}/>*/}
                    {/*<button variant="primary" onClick={this.changeScreen}>Change Screen</button>*/}
                    <a href="#menu">
                        <img src="images/menuButton.png" alt="" />
                    </a>    
                </div >
            );
        }
        if (screen == 1) {
            return (
                <div >
                    <WebcamComponent />
                    {/*<button variant="primary" onClick={this.changeScreen}>Change Screen</button>*/}
                    <a href="#menu">
                        <img src="images/menuButton.png" alt="" />
                    </a>
                </div >
            );
        }
    }
}

export default App;
