    changeScreen = (screen) => {
        console.log(screen);
        return 0;
    }
    

    render() {
        const screen = this.changeScreen(screen);
        console.log(screen);
        if (screen == 0) {
            return (
                <div >
                    <DisplayText /> {/*callbackFromParent={this.myCallback}/>*/}
                    <button variant="primary" onClick={this.changeScreen}></button>
                </div >
