import React, { Component } from 'react';
import './components/stylesheet.css';
import WebcamComponent from './components/webcamComponent'
import DisplayText from './components/displayText'
import MenuComponent from './components/menuComponent'
import hamButton from './hamButton.png';
import crossButton from './crossButton.png';

class App extends Component {
    
    constructor(props) {
        super(props);
        this.screen = 0;
        this.ham_button_ref = React.createRef();
        this.cross_button_ref = React.createRef();
        this.menu_ref = React.createRef();
        this.MenuComponent = React.createRef();
        this.text_ref = React.createRef();
        this.textState = false;
        this.text = "Open the menu to add some text..."
    }

    changeScreen = () => {
        switch (this.screen) {
            case 0:
                this.screen += 1;
                break
            case 1:
                this.screen -= 1;
                break
            default:
                break
        }
        //this.MenuComponent.current.showHamButton();
        this.showHamButton();
        this.forceUpdate();
    }

    getScreen = () => {
        return this.screen;
    }

    /*getMenuState = () => {
        return this.menuState;
    }*/

    showHamButton = () => {
        const hamNode = this.ham_button_ref.current;
        const menuNode = this.menu_ref.current;
        hamNode.style.visibility = "visible";
        menuNode.style.visibility = "hidden";
        this.forceUpdate();
    }

    showMenu = () => {
        const hamNode = this.ham_button_ref.current;
        const menuNode = this.menu_ref.current;
        hamNode.style.visibility = "hidden";
        menuNode.style.visibility = "visible";
        this.forceUpdate();
    }

    getTextState = () => {
        return this.textState;
    }

    setText = () => {
        console.log("setText()");
        const textNode = this.text_ref.current;
        textNode.style.visibility = "hidden";
        if (this.textState) {
            textNode.style.color = "black";
        } else {
            textNode.style.color = "grey";
        }
    }

    /*selectText = () => {
        console.log("Selecting all Text");
        const textNode = this.text_ref.current;
        document.execCommand("copy", true, null);
    }*/

    /*copyText = () => { 
        this.textState = false;
        this.text = "Open the menu to add some text...";
        this.setText();
    }*/

    render() {
        const screen = this.getScreen();
        //const menuState = this.getMenuState();
        //const textState = this.getTextState();

        if (screen === 0) {
            return (
                <div >
                    <DisplayText ref={this.text_ref} />
                    {/*<MenuComponent/>*/}

                        <img ref={this.ham_button_ref} className="hamImage" src={hamButton} onClick={this.showMenu} alt="" />
                        <div ref={this.menu_ref} className="menu">
                            <img ref={this.cross_button_ref} className="crossImage" src={crossButton} onClick={this.showHamButton} alt="" />
                            <p className="menuText" id="menuOne" onClick={this.changeScreen}>Scan Text</p>
                            <p className="menuText">View Text</p>
                            <p className="menuText" >Select All Text</p>
                        </div>
                    
                </div >
                );
            }
        if (screen === 1) {
            return (
                <div >

                        <img ref={this.ham_button_ref} className="hamImage" src={hamButton} onClick={this.showMenu} alt="" />
                        <div ref={this.menu_ref} className="menu">
                            <img ref={this.cross_button_ref} className="crossImage" src={crossButton} onClick={this.showHamButton} alt="" />
                            <p className="menuText" id="menuOne" >Scan Text</p>
                            <p className="menuText" onClick={this.changeScreen}>View Text</p>
                            <p className="menuText" >Select All Text</p>
                        </div>

                        {/*<MenuComponent />*/}
                            <div className="webcamContainer" visibility="visible">
                                <WebcamComponent />
                            </div>
                </div >
                            );
                        }
                
                        this.setText() ; // NOT WORKING
                
        {/*if (menuState) {
            this.showMenu() ;
        } else {
            this.showHamButton() ;
        }*/}
    }
}

export default App;
